sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var body = request.body.data;
    var companyName = body.company;
    var shortDesc = body.short_description;

    var result = {};

    try {
        // Lookup company
        var grCompany = new GlideRecord('core_company');
        grCompany.addQuery('name', companyName);
		gs.info('Before Query ' + companyName);
        grCompany.query();
        gs.info('After Query: ' + companyName);
        if (grCompany.next()) {
            gs.info('Could read the company with this user ' + companyName);
            // Create new incident
            var grIncident = new GlideRecord('incident');
            gs.info('Could read the Incident with this user ' + companyName);
            grIncident.initialize();
            grIncident.short_description = shortDesc;
            grIncident.caller_id = 'Platform User';
            grIncident.company = grCompany.sys_id;
            grIncident.sys_domain = grCompany.domain; // domain reference comes from core_company
            grIncident.insert();
            gs.info('Can insert incident ');

            let correlationId = grIncident.number;
            gs.info('Domain Indcident API: inserted incident number: ' + correlationId);
            result.status = "success";
            result.incident_sys_id = correlationId;
            result.company = companyName;
            result.domain = grCompany.sys_domain.getDisplayValue();

        } else {
            response.setStatus(404);
            result.status = "error";
            result.message = "Company not found: " + companyName;
        }
    } catch (e) {
        response.setStatus(500);
        result.status = "error";
        result.message = e.message;
    }

    response.setBody(result);


})(request, response);
